mapper.select:
select n.ncr_id, n.ncr_title,n.ncr_comp , a.vendor_id, v.vendor_name, m.member_name as issuer_name ,g.ncr_grade_name, n.audit_id from ncr_info n , audit_info a, member_info m ,ncr_grade g , vendor_info v where n.audit_id=a.audit_id and n.issuer_id=m.member_id and n.ncr_grade_id=g.ncr_grade_id and v.vendor_id=a.vendor_id
and n.ncr_id = #{ncr_id}
and n.ncr_title like '%' || #{ncr_title} || '%'
and v.vendor_name like '%' ||#{vendor_id} || '%'
and m.member_name like '%' ||#{issuer_id} || '%'
and n.ncr_comp= #{ncr_comp}
and a.audit_id= #{audit_id}
