mapper.select:
   				
		 select n.ncr_id, n.ncr_title,n.ncr_comp , a.vendor_id, m.member_name as issuer_name ,g.ncr_grade_name, n.audit_id 
		from ncr_info n , audit_info a, member_info m  ,ncr_grade g
     	where   n.audit_id=a.audit_id and n.issuer_id=m.member_id and n.ncr_grade_id=g.ncr_grade_id
						
			
				and n.ncr_id = #{ncr_id}
			
			
			
				and n.ncr_title like '%' || #{ncr_title} || '%'
			
			
				 and a.vendor_id=#{vendor_id}
			
			
				and n.issuer_id=#{issuer_id}
